{{Infobox company | name = EuropeanDataWarehouse | logo = European Data Warehouse Logo.png | logo_size = 250px | logo_alt = | logo_caption = European DataWarehouse | logo_padding = | image = | image_size = | image_alt = | image_caption = | trading_name = | native_name = | native_name_lang = <!-- Use ISO 639-1 code, e.g. "fr" for French. For multiple names in different languages, use {{Lang|[code]|[name]}}. --> | romanized_name = | former_name = | type = | traded_as = | ISIN = | ISIN2 = | industry = [[Data Warehousing]] | genre = | fate = | predecessor = <!-- or: | predecessors = --> | successor = <!-- or: | successors = --> | founded = January 2012 | founder = <!-- or: | founders = --> | defunct = <!-- {{End date|YYYY|MM|DD}} --> | hq_location = | hq_location_city = [[Frankfurt]] | hq_location_country = [[Germany]] | num_locations = | num_locations_year = <!-- Year of num_locations data (if known) --> | area_served = <!-- or: | areas_served = --> | key_people = Christian Thun <small>CEO</small> | products = | brands = | production = | production_year = <!-- Year of production data (if known) --> | services = | revenue = | revenue_year = <!-- Year of revenue data (if known) --> | operating_income = | income_year = <!-- Year of operating_income data (if known) --> | net_income = <!-- or: | profit = --> | net_income_year = <!-- or: | profit_year = --><!-- Year of net_income/profit data (if known) --> | aum = <!-- Only for financial-service companies --> | assets = | assets_year = <!-- Year of assets data (if known) --> | equity = | equity_year = <!-- Year of equity data (if known) --> | owner = | members = | members_year = <!-- Year of members data (if known) --> | num_employees = | num_employees_year = <!-- Year of num_employees data (if known) --> | parent = | divisions = | subsid = | module = <!-- Used to embed other templates --> | ratio = <!-- Basel III ratio, for BANKS ONLY --> | rating = <!-- credit rating, for BANKS ONLY --> | website = {{URL|https://eurodw.eu/