{{refimprove|date=June 2011}} {{Infobox government agency | agency_name = Jet Propulsion Laboratory Science Division | type = | nativename = | nativename_a = | nativename_r = | seal = Jet Propulsion Laboratory logo.svg | seal_width = 150px | seal_caption = | logo = NASA logo.svg | logo_width = 100px | logo_caption = | picture = Site du JPL en Californie.jpg | picture_width = | picture_caption = Aerial view of the Jet Propulsion Laboratory in [[La Cañada Flintridge, California]] | formed = {{Start date|1936}} <!-- {{Start date|YYYY|MM|DD}} OR {{Start date and age|YYYY|MM|DD}} --> | preceding1 = | preceding2 = <!-- up to |preceding6= --> | dissolved = | superseding = | agency_type = [[Earth|Terrestrial]] and [[Outer space|extraterrestrial]] sciences | jurisdiction = [[United States]] | headquarters = [[Pasadena, California]] | coordinates = {{coord|34.201060|-118.172453|type:landmark_region:US|display=inline,title}} | motto = | employees = | budget = | chief1_name = Simon J. Hook | chief1_position = Division Manager<ref>{{cite web|title=JPL's Science Division: People|url=https://science.jpl.nasa.gov/people/Hook/|website=JPL Science Division|publisher=NASA |accessdate=12 January 2018 |deadurl=yes |archiveurl=https://web.archive.org/web/20180112171323/https://science.jpl.nasa.gov/people/Hook/ |archivedate= 12 January 2018 |df= }}</ref> | chief2_name = Janis L. Chodas | chief2_position = Director for Engineering and Science<ref name=ExecCoun>{{cite web|title=JPL Executive Council |url=https://www.jpl.nasa.gov/about/exec.php |website=JPL Science Division |publisher=NASA |accessdate=12 January 2018 |deadurl=yes |archiveurl=https://web.archive.org/web/20180112170516/https://www.jpl.nasa.gov/about/exec.php |archivedate= 12 January 2018 |df= }}</ref> | chief3_name = Diane L. Evans | chief3_position = Director for Earth Science and Technology<ref name=ExecCoun/> | chief4_name = Mark Simons | chief4_position = Chief Scientist<ref name=ExecCoun/> | chief5_name = Fred Hadaegh | c