{{Use dmy dates|date=October 2013}} {{Infobox artist | name = Jeremy Gilbert-Rolfe | image = Jeremy_Gilbert-Rolfe.jpg | image_size = | alt = | caption = | birth_name = | birth_date = {{Birth date and age|df=yes|1945|08|04}} | birth_place = [[Tunbridge Wells]], [[England]] | death_date = <!-- {{Death date and age|df=yes|YYYY|MM|DD|YYYY|MM|DD}} --> | death_place = | nationality = | spouse = | field = [[Painting]] | training = | movement = Geometric Abstraction | works = | patrons = | awards = | elected = | website = {{URL|http://www.jeremygilbert-rolfe.com}}
