| year   = c. 710–750{{sfn|Drayson|2006|p=121}} 
| type   = [[Fresco]] painting
| coordinates = {{coord|31.801935|36.57663|display=inline}}
| height_metric = 
| condition = damaged
| city   = [[Qasr Amra]], [[Jordan]]
