| || || || || || || || || || || || || || || || ||{{Iso1|155|Gd|R}}||{{Iso1|156|Gd|R}}||{{Iso1|157|Gd|R}}||{{Iso1|158|Gd|R}}||{{Iso1|159|Gd}}||{{Iso1|160|Gd|R}}
