{{Infobox officeholder | honorific-prefix = | name = Jan Didriksen | native_name = | native_name_lang = | honorific-suffix = | image = | image_size = | smallimage = <!--If this is specified, "image" should not be.--> | alt = | caption = | order = | office = Chief executive of the Federation of Norwegian Industries | term_start = 1965 | term_end = 1982 | alongside = <!--For two or more people serving in the same position from the same district. (e.g. United States Senators.)--> | monarch = | president = | governor_general = | primeminister = | taoiseach = | chancellor = | governor = | vicepresident = | viceprimeminister = | deputy = | lieutenant = | succeeding = <!--For President-elect or equivalent--> | constituency = | majority = | predecessor = Knut Hald | successor = | prior_term = | order2 = <!--Can be repeated up to 16 times by changing the number--> | office2 = <!--Can be repeated up to 16 times by changing the number--> | term_start2 = <!--Can be repeated up to 16 times by changing the number--> | term_end2 = <!--Can be repeated up to 16 times by changing the number--> | alongside2 = <!--Can be repeated up to 16 times by changing the number--> | monarch2 = <!--Can be repeated up to 16 times by changing the number--> | president2 = <!--Can be repeated up to 16 times by changing the number--> | governor_general2 = <!--Can be repeated up to 16 times by changing the number--> | primeminister2 = <!--Can be repeated up to 16 times by changing the number--> | chancellor2 = <!--Can be repeated up to 16 times by changing the number--> | taoiseach2 = <!--Can be repeated up to 16 times by changing the number--> | governor2 = <!--Can be repeated up to 16 times by changing the number--> | vicepresident2 = <!--Can be repeated up to 16 times by changing the number--> | viceprimeminister2 = <!--Can be repeated up to 16 times by changing the number--> | deputy2 = <!--Can be repeated up to 16 times by changing the number--> | lieutenant2 = <!--Can be repeated up to 16 times by