  \hat{\mathbf x} &= \frac{x \left(\sqrt{x^2 + y^2} \hat{\mathbf r} + z \hat{\boldsymbol \theta}\right) - y \sqrt{x^2 + y^2 + z^2} \hat{\boldsymbol \varphi}}{\sqrt{x^2 + y^2} \sqrt{x^2 + y^2 + z^2}} \\
