{{Infobox company | name = RailTel Corporation of India Ltd. | logo = File:Railtel Logo.svg | logo_size = 120px | logo_alt = | logo_caption = | logo_padding = | image = | image_size = | image_alt = | image_caption = | trading_name = | native_name = | native_name_lang = hi | former_name = | type = [[Public sector undertakings in India|Public sector undertaking (PSU)]] | traded_as = | ISIN = | industry = [[Telecommunications]] | fate = | successor = <!-- or: | successors = --> | founded = {{start date and age|df=yes|2000|09}}<ref>{{cite web|title=Evolution of Railtel|url=http://www.railtelindia.com/index.php?option=com_content&view=article&id=197&Itemid=83&lang=en|archive-url=https://web.archive.org/web/20140215053800/http://www.railtelindia.com/index.php?option=com_content&view=article&id=197&Itemid=83&lang=en|dead-url=yes|archive-date=15 February 2014|website=www.railtelindia.com|accessdate=22 January 2016}}</ref> | founder = <!-- or: | founders = --> | defunct = <!-- {{end date|df=yes|YYYY|MM|DD}} --> | hq_location = 143, Institutional Area, Sector-44 | hq_location_city = [[Gurgaon]], Haryana | hq_location_country = India | num_locations = | num_locations_year = <!-- Year of num_locations data (when known) --> | area_served = [[India]] | key_people = Ashutosh Vasant, Chairman & Managing Director (Addl Charge)and Director POM) | products = | brands = | services = Telepresence services, Data Centre Services, MPLS-VPN, Internet Leased Line, Consultancy Services, Signalling Services, Adhaar based service | revenue = {{nowrap|{{profit}}{{INRConvert|5.54|b}}}} | revenue_year = 2014–15<ref name="railtelindia1">{{cite web|title=15th Annual Report 2014-2015|url=http://www.railtelindia.com/financial_results/ae9dbcc5dc7097e86a1bed0b3913865e__Annual-Report-RailTel-2014-15-Final.pdf|archive-url=https://web.archive.org/web/20160128035041/http://www.railtelindia.com/financial_results/ae9dbcc5dc7097e86a1bed0b3913865e__Annual-Report-RailTel-2014-15-Final.pdf|dead-url=yes|archive-da